// === Meteo: pioggia 6h + totale 6d (3g passati + 3g futuri) ===
const t   = msg.payload.hourly.time;
const r   = msg.payload.hourly.rain || msg.payload.hourly.precipitation;
const now = new Date();

// prossime 6h
let rain6h = 0;
let idx = t.findIndex(s => new Date(s) >= now);
if (idx < 0) idx = 0;
for (let i = 0; i < 6 && idx + i < r.length; i++) {
  rain6h += Number(r[idx + i]);
}


let past = 0, future = 0;
// 5 giorni passati + 3 giorni futuri
for (let i = 0; i < t.length; i++) {
  const d = new Date(t[i]);
  // @ts-ignore
  const diff = d - now;
  if (diff < 0 && Math.abs(diff) <= 5 * 24 * 60 * 60 * 1000) past += Number(r[i]);
  if (diff >= 0 && diff <= 3 * 24 * 60 * 60 * 1000) future += Number(r[i]);
}


msg.payload = { rain6h, rainTotal6d: past + future };
msg.topic   = 'weather';
return msg;

// ┌─────────────────────────────────────────────────────────────────────┐
// │  Function: Decisione irrigazione con temperatura e umidità        │
// └─────────────────────────────────────────────────────────────────────┘

const ha    = global.get('homeassistant').homeAssistant.states;

// — soglie “classiche”
const enable    = ha['input_boolean.irrigation_enable']?.state === 'on';
const force     = ha['input_boolean.force_irrigation']?.state === 'on';
const rain6hT   = Number(ha['input_number.irrigation_rain_threshold']?.state)   || 2;
const rainTot   = Number(ha['input_number.irrigation_total_rain_limit']?.state) || 20;
const soilT     = Number(ha['input_number.irrigation_soil_threshold']?.state)   || 30;
const dur       = Number(ha['input_number.irrigation_duration_zone1']?.state)   || 600;

// — nuove soglie temperatura/umidità atmosferica
const tempMin   = Number(ha['input_number.irrigation_temp_min']?.state)   || 10;
const tempMax   = Number(ha['input_number.irrigation_temp_max']?.state)   || 35;
const humMin    = Number(ha['input_number.irrigation_hum_min']?.state)    || 30;

// — toggle suolo
const sensorEnabled = ha['input_boolean.irrigation_sensor_enable']?.state === 'on';

// — destrutturo DAL payload solo ciò che serve
const {
  avgTempToday,
  avgTempYesterday,
  avgHumToday,
  avgHumYesterday,
  soilMoist      // <-- ATTENZIONE: lo estraiamo da msg.payload!
} = msg.payload;

// — estraggo pioggia dal payload.weather (con fallback a 0)
const weather     = msg.payload.weather || {};
const rain6h      = Number(weather.rain6h      || 0);
const rainTotal6d = Number(weather.rainTotal6d || 0);

// — se il sensore suolo è spento, ignoro il valore: altrimenti prendo soilMoist
const soil = sensorEnabled
  ? Number(soilMoist) || 0
  : null;

// — timestamp per i log
const now = Date.now();
const tss = new Date(now).toLocaleString('it-IT', {
  day:   '2-digit', month:'2-digit',
  hour:  '2-digit', minute:'2-digit', second:'2-digit'
});

// === 1) Disabilitato globalmente o bypass? ===
if (!enable && !force) {
  node.status({ fill:'grey', shape:'ring', text:'Disabilitato' });
  return [ null, { payload:{ reason:`[${tss}] Irrigazione disabilitata` } } ];
}

// === 2) Skip programmato o cumulata 6d > soglia? ===
const skipKey   = 'skipUntil_z1';
const skipUntil = flow.get(skipKey) || 0;
if (!force) {
  if (skipUntil && now < skipUntil) {
    node.status({ fill:'red', shape:'ring', text:`Skip fino ${new Date(skipUntil).toLocaleDateString()}` });
    return [ null, { payload:{ reason:`[${tss}] Skip programmato fino a ${new Date(skipUntil).toLocaleDateString()}` } } ];
  }
  if (rainTotal6d > rainTot) {
    flow.set(skipKey, now + 2*24*60*60*1000);
    node.status({ fill:'red', shape:'ring', text:`Salto 2g – pioggia ${rainTotal6d} mm` });
    return [ null, { payload:{ reason:`[${tss}] Pioggia cumulata > soglia (${rainTotal6d} mm)` } } ];
  }
}

// === 3) Controllo temperatura media oggi ===
if (avgTempToday != null && (avgTempToday < tempMin || avgTempToday > tempMax)) {
  node.status({ fill:'orange', shape:'ring', text:`Temp fuori range: ${avgTempToday.toFixed(1)}°C` });
  return [ null, { payload:{ reason:`[${tss}] Temperatura media ${avgTempToday.toFixed(1)}°C fuori range [${tempMin}–${tempMax}]` } } ];
}

// === 4) Controllo umidità atmosferica media oggi ===
if (avgHumToday != null && avgHumToday < humMin) {
  node.status({ fill:'orange', shape:'ring', text:`Umidità bassa: ${avgHumToday.toFixed(0)}%` });
  return [ null, { payload:{ reason:`[${tss}] Umidità media ${avgHumToday.toFixed(0)}% < soglia ${humMin}%` } } ];
}

// === 5) Branch irrigazione ===
if (
  force
  || (rain6h < rain6hT && (sensorEnabled ? soil < soilT : true))
) {
  node.status({ fill:'green', shape:'dot', text:`Irrigo ${dur}s` });
  return [
    {
      payload: { duration: dur },
      delay: dur * 1000  // <<<<<< chiave per il nodo delay
    },
    {
      payload: {
        reason: `[${tss}] Irrigazione avviata: durata ${dur}s`
      }
    }
  ];
}

// === 6) Default skip con motivazioni chiare ===
let reasons = [];

// troppa pioggia nelle ultime 6h?
if (rain6h >= rain6hT) {
  reasons.push(`pioggia ${rain6h} mm ≥ ${rain6hT} mm`);
}
// terreno troppo umido?
if (sensorEnabled && soil !== null && soil >= soilT) {
  reasons.push(`umidità suolo ${soil}% ≥ ${soilT}%`);
}
// se non ho ancora riempito motivi, spiego il caso misto
if (reasons.length === 0) {
  if (sensorEnabled) {
    if (rain6h < rain6hT) {
      reasons.push(`umidità ok ma pioggia insufficiente (${rain6h} mm < ${rain6hT} mm)`);
    } else {
      reasons.push(`pioggia ok ma terreno troppo umido (${soil}% ≥ ${soilT}%)`);
    }
  } else {
    reasons.push(`pioggia ${rain6h} mm ≥ soglia ${rain6hT} mm`);
  }
}

const text = reasons.join(' & ');
node.status({ fill:'red', shape:'ring', text:`Skip: ${text}` });
return [
  null,
  { payload:{ reason:`[${tss}] Skip: ${text}` } }
];

// Function “Elabora next run” (gestione anche di slot rimossi)

// 1) se cron-plus ha restituito un errore “not found”, pulisco il valore e esco
if (msg.payload.error && msg.payload.error.includes('not found')) {
  msg.payload = { data: { value: "Errore cronPlus" } };
  return msg;
}

// 2) individua dove cron-plus ti ha messo lo status
// può essere in msg.payload.result.status oppure in msg.payload.status
const st = msg.payload?.result?.status ?? msg.payload?.status;
if (!st) {
  // nessuno status valido → svuoto e esco
  msg.payload = { data: { value: "Irrigazione automatica Disabilitata" } };
  return msg;
}

// 3) estrai le due descrizioni (o stringhe vuote se mancanti)
const nd  = st.nextDescription  || st.nextDescr  || "";
const sch = st.description      || "";

// 4) traduci “in X hours Y minutes Z seconds” → “Tra X ore Y minuti Z secondi”
let tradNext = nd
  .replace(/^in\s+/,          "Tra ")
  .replace(/(\d+)\s*hours?/,   "$1 ore")
  .replace(/(\d+)\s*minutes?/, "$1 minuti")
  .replace(/(\d+)\s*seconds?/, "$1 secondi");

// 5) traduci la parte cron → “Alle hh:mm, ogni ...”
let tradSch = sch
  .replace(/^At\s+/,        "Alle ")
  .replace(/\bevery\b/,     "ogni")
  .replace(/\bMON|TUE|WED|THU|FRI|SAT|SUN\b/g, d => {
    const m = { MON:"lun", TUE:"mar", WED:"mer",
                THU:"gio", FRI:"ven", SAT:"sab", SUN:"dom" };
    return m[d] || d;
  });

// 6) componi la frase finale
const text = [ tradNext.trim(), tradSch.trim() ]
  .filter(t => t)         // elimino eventuali stringhe vuote
  .join(" — ");           // “Tra ... — Alle ...”

// 7) impacchetto per input_text.set_value
msg.payload = { data: { value: text } };
return msg;

// ──────────────────────────────────────────────────────────────────────────
// Function “Calcola medie temperatura + umidità giorni correnti”
// ──────────────────────────────────────────────────────────────────────────┘

const data = msg.payload.hourly;
const now  = new Date();

// Estrai i due array di time e temperature/humidity
const times = data.time.map(t => new Date(t));
const temps = data.temperature_2m;
const hums  = data.relative_humidity_2m;

// Calcola indice di inizio e fine per “oggi” e “ieri”
const startOfToday      = new Date(now.getFullYear(), now.getMonth(), now.getDate());
const startOfYesterday  = new Date(startOfToday);
startOfYesterday.setDate(startOfToday.getDate() - 1);

let sumTodayTemp = 0, countTodayTemp = 0;
let sumYestTemp  = 0, countYestTemp  = 0;
let sumTodayHum  = 0, countTodayHum  = 0;
let sumYestHum   = 0, countYestHum   = 0;

for (let i = 0; i < times.length; i++) {
  const t = times[i];
  if (t >= startOfToday) {
    sumTodayTemp += temps[i];
    sumTodayHum  += hums[i];
    countTodayTemp++;
    countTodayHum++;
  } else if (t >= startOfYesterday && t < startOfToday) {
    sumYestTemp += temps[i];
    sumYestHum  += hums[i];
    countYestTemp++;
    countYestHum++;
  }
}

// Costruisci weather con pioggia già calcolata dal nodo precedente
const weather = msg.payload.weather || { rain6h: 0, rainTotal6d: 0 };

msg.payload = {
  // porto avanti il payload.weather
  weather: weather,
  avgTempToday:     countTodayTemp ? sumTodayTemp / countTodayTemp : null,
  avgTempYesterday: countYestTemp  ? sumYestTemp  / countYestTemp  : null,
  avgHumToday:      countTodayHum  ? sumTodayHum  / countTodayHum   : null,
  avgHumYesterday:  countYestHum   ? sumYestHum   / countYestHum    : null,
  soilMoist:        msg.payload.soilMoist
};

return msg;

//PRENDO ANCHE LA DURATION DELL'IRRIGAZIONE
const ha    = global.get('homeassistant').homeAssistant.states;
const dur   = Number(ha['input_number.irrigation_duration_zone1']?.state)   || 600;

// Helper per testare se è un numero valido
function num(v) {
  return (v !== null && v !== undefined && !isNaN(v)) ? Number(v) : null;
}

// Costruisco il campo measurement
msg.measurement = 'irrigation_metrics';

// (Opzionale) tagga la zona, es. “slot1”
msg.tags = {
  slot: msg.topic   // se msg.topic era “slot1”
};

// Preparo i fieldset filtrando i NaN
msg.payload = {
  rain_6h:           num(msg.payload.slot1.weather.rain6h),
  rain_6d:           num(msg.payload.slot1.weather.rainTotal6d),
  soil_moist:        num(msg.payload.soilMoist),
  temp_today:        num(msg.payload.slot1.avgTempToday),
  temp_yesterday:    num(msg.payload.slot1.avgTempYesterday),
  hum_today:         num(msg.payload.slot1.avgHumToday),
  hum_yesterday:     num(msg.payload.slot1.avgHumYesterday),
  durationIrrigazione: num(dur)
};

// Se vuoi, puoi rimuovere i campi null per renderlo più pulito:
Object.keys(msg.payload).forEach(k => {
  if (msg.payload[k] === null) delete msg.payload[k];
});

// InfluxDB out si aspetta:
// - msg.measurement   “nome_della_misura”
// - msg.payload       oggetto con solo campi numerici
// - msg.tags          (facoltativo) tag string/string

return msg;

const ha = global.get('homeassistant').homeAssistant.states;
const dur = Number(ha['input_number.irrigation_duration_zone1']?.state) || 600;

// Helper per numeri validi
function num(v) {
  return (v !== null && v !== undefined && !isNaN(v)) ? Number(v) : '';
}

// Estrai i dati dal payload originale
const rain_6h        = num(msg.payload.slot1.weather.rain6h);
const rain_6d        = num(msg.payload.slot1.weather.rainTotal6d);
const soil_moist     = num(msg.payload.soilMoist);
const temp_today     = num(msg.payload.slot1.avgTempToday);
const temp_yesterday = num(msg.payload.slot1.avgTempYesterday);
const hum_today      = num(msg.payload.slot1.avgHumToday);
const hum_yesterday  = num(msg.payload.slot1.avgHumYesterday);
const duration       = num(dur);

// Costruisci una riga CSV
const row = [
  new Date().toISOString(),
  rain_6h,
  rain_6d,
  soil_moist,
  temp_today,
  temp_yesterday,
  hum_today,
  hum_yesterday,
  duration
];

msg.payload = row.join(",");
msg.filename = "/share/irrigation_dataset.csv";

return msg;

const now = Date.now();
const next = Date.parse(msg.payload.result.status.nextDate);

if (!next || isNaN(next)) {
    node.warn("Data non valida da cronplus.");
    return null;
}

const diffSec = Math.floor((next - now) / 1000);

if (diffSec > 0 && diffSec <= 86400) {
    const hrs = String(Math.floor(diffSec / 3600)).padStart(2, '0');
    const mins = String(Math.floor((diffSec % 3600) / 60)).padStart(2, '0');
    const secs = String(diffSec % 60).padStart(2, '0');

    const duration = `${hrs}:${mins}:${secs}`;

    msg.payload = {
        data:{
            duration: duration,
        }
    };
return msg;

} else {
msg.payload = "reset";
return msg

}

let pioggia6h = msg.payload.rain6h;    // valore in mm
