// ┌─────────────────────────────────────────────────────────────────────┐
// │ Function: update cron slot1 (fix parsing time + cron fields)      │
// └─────────────────────────────────────────────────────────────────────┘

// leggo da HA
const ha      = global.get('homeassistant').homeAssistant.states;
const rawTime = ha['input_datetime.irrigation_slot1_time']?.state || '00:00';

// estraggo solo HH:MM (scarto data e secondi)
const timePart = rawTime.includes(' ')
  ? rawTime.split(' ')[1].slice(0,5)
  : rawTime.slice(0,5);

const [h, m] = timePart.split(':');
const daysStr = (ha['input_select.irrigation_slot1_days']?.state || '').toLowerCase();
const slotEn  = ha['input_boolean.irrigation_slot1_enable']?.state === 'on';
const globalEn= ha['input_boolean.irrigation_enable']?.state === 'on';

// 1) se disabilitato globalmente o di slot → remove
if (!globalEn || !slotEn) {
  return {
    topic: 'slot1',
    payload: {
      command: 'remove',
      name:    'slot1'
    }
  };
}

// mappa italiano → DOW
const mapIt = {
  lunedi:    'MON',
  martedi:   'TUE',
  mercoledi: 'WED',
  giovedi:   'THU',
  venerdi:   'FRI',
  sabato:    'SAT',
  domenica:  'SUN'
};

let expr;

// tutti i giorni
if (daysStr === 'tutti i giorni') {
  expr = `0 ${m} ${h} * * *`;
}
// giorni pari
else if (daysStr === 'giorni pari') {
  expr = `0 ${m} ${h} 2-30/2 * *`;
}
// giorni dispari
else if (daysStr === 'giorni dispari') {
  expr = `0 ${m} ${h} 1-31/2 * *`;
}
// ogni 3 giorni
else if (daysStr === 'ogni 3 giorni') {
  expr = `0 ${m} ${h} 1-31/3 * *`;
}
// ogni 4 giorni
else if (daysStr === 'ogni 4 giorni') {
  expr = `0 ${m} ${h} 1-30/4 * *`;
}
// ogni 5 giorni
else if (daysStr === 'ogni 5 giorni') {
  expr = `0 ${m} ${h} 1-30/5 * *`;
}
// lista DOW
else {
  const arr = daysStr
    .split(',')
    .map(d => d.trim())
    .filter(d => mapIt[d])
    .map(d => mapIt[d]);
  const dow = arr.length ? arr.join(',') : '*';
  expr = `0 ${m} ${h} * * ${dow}`;
}

// invio comando a cron-plus
return {
  topic: 'slot1',
  payload: {
    command:        'add',
    name:           'slot1',
    expressionType: 'cron',
    expression:     expr,
    payloadType:    'default',
    payload:        'schedule'
  }
};

// Function “Chiedi stato slot1”
const expr = flow.get('slot1_expr') || '* * * * *';
msg.topic   = 'status';
msg.payload = 'slot1';
return msg;

let csv = "time,rain_6h,rain_6d,soil_moist,temp_today,temp_yesterday,hum_today,hum_yesterday,durationIrrigazione\n";

function num(n) {
    return typeof n === "number" ? n : Number(n) || 0;
}

msg.payload.forEach(row => {
    const time = new Date(row.time).toISOString(); 
    const rain_6h = num(row.rain_6h);
    const rain_6d = num(row.rain_6d);
    const soil_moist = num(row.soil_moist);
    const temp_today = num(row.temp_today);
    const temp_yesterday = num(row.temp_yesterday);
    const hum_today = num(row.hum_today);
    const hum_yesterday = num(row.hum_yesterday);
    const durationIrrigazione = num(row.durationIrrigazione);

    csv += `${time},${rain_6h},${rain_6d},${soil_moist},${temp_today},${temp_yesterday},${hum_today},${hum_yesterday},${durationIrrigazione}\n`;
});

msg.payload = csv;
msg.filename = "/share/irrigation_dataset.csv";
return msg;
